const data = $input.first().json;
const events = [];

if (data.success === 'true' && data.records && data.records.TideForecast) {
  for (const forecast of data.records.TideForecast) {
    const location = forecast.Location;
    const timePeriods = forecast.TimePeriods;

    if (timePeriods && timePeriods.Daily) {
      for (const daily of timePeriods.Daily) {
        const date = daily.Date;

        if (daily.TideRange) {
          for (const tide of daily.TideRange) {
            const tideType = tide.Tide; // æ»¿æ½® or ä¹¾æ½®
            const tideTime = tide.TideTime;
            const heights = tide.TideHeights || {};
            const height = heights.AboveLocalMSL || heights.AboveTWVD || '';

            if (tideTime) {
              const datetime = `${date}T${tideTime}:00`;
              const emoji = tideType === 'æ»¿æ½®' ? 'ğŸ”º' : 'ğŸ”»';

              events.push({
                datetime,
                title: `${emoji} ${tideType}${height ? ' ' + height + 'cm' : ''}`,
                description: `ç«™é»: ${location.LocationName}\næ½®ä½: ${height}cm`,
                type: tideType,
                height
              });
            }
          }
        }
      }
    }
  }
}

// ç”¢ç”Ÿ iCal æ ¼å¼
const icalLines = [
  'BEGIN:VCALENDAR',
  'VERSION:2.0',
  'PRODID:-//å°ç£æ½®æ±æ—¥æ›†//n8n//TW',
  'CALSCALE:GREGORIAN',
  'METHOD:PUBLISH',
  'X-WR-CALNAME:ğŸŒŠ åŸºéš†æ½®æ±',
  'X-WR-TIMEZONE:Asia/Taipei'
];

for (const event of events) {
  const dt = event.datetime.replace(/[-:]/g, '').replace('T', 'T');
  const uid = `${dt}-${event.type}@tide.tw`;

  icalLines.push('BEGIN:VEVENT');
  icalLines.push(`DTSTART;TZID=Asia/Taipei:${dt}`);
  icalLines.push(`SUMMARY:${event.title}`);
  icalLines.push(`DESCRIPTION:${event.description.replace(/\n/g, '\\n')}`);
  icalLines.push(`UID:${uid}`);
  icalLines.push('END:VEVENT');
}

icalLines.push('END:VCALENDAR');

return [{
  json: {
    eventCount: events.length,
    icalContent: icalLines.join('\r\n')
  },
  binary: {
    data: {
      data: Buffer.from(icalLines.join('\r\n')).toString('base64'),
      mimeType: 'text/calendar',
      fileName: 'tide.ics'
    }
  }
}];
